    # Verify base is within the allowed range
    if not 2 <= source_base <= 36 or not 2 <= target_base <= 36:
        raise ValueError("Base must be between 2 and 36")

    # Verify if each character in the number is valid for the source base
    try:
        # Convert from source base to base 10
        decimal_number = int(number, source_base)
    except ValueError as e:
        raise ValueError(f"Number has invalid digits for base {source_base}") from e

    # Convert from base 10 to target base
    if decimal_number == 0:
        return '0'
    digits = []
    while decimal_number > 0:
        remainder = decimal_number % target_base
        if 0 <= remainder < 10:
            digits.append(str(remainder))
        else:
            digits.append(chr(remainder - 10 + ord('A')))
        decimal_number //= target_base
    # Return the converted number in reverse order since it was computed backwards
    return ''.join(reversed(digits))
