        lambda x: ('Low', 'Negative') if x < 0 else ('Neutral', 'Zero') if x == 0 else ('High', 'Positive') if x > 0 else None,
        lambda x: ('Short', 'Short') if len(x) < 5 else ('Medium', 'Medium') if len(x) >= 5 and len(x) <= 10 else ('Long', 'Long') if len(x) > 10 else None,
        lambda x: ('Single', 'Single') if len(x) == 1 else ('Few', 'Few') if len(x) >= 2 and len(x) <= 5 else ('Many', 'Many') if len(x) > 5 else None
    ]

