def hierarchical_replacement_creation(reference_chart, template):
    def replace(placeholders, level_chart):
        for key, value in level_chart.items():
            placeholders = placeholders.replace('{' + key + '}', value)
        return placeholders

    # Key check and pre-replacement
    for placeholder in reference_chart:
        if '{' in reference_chart[placeholder]:
            reference_chart[placeholder] = replace(reference_chart[placeholder], reference_chart)

    # Final replacement
    resolved_string = replace(template, reference_chart)

    # Check for unresolved placeholders
    if '{' in resolved_string:
        return None

    # Remove 'e' characters
    final_string = resolved_string.replace('e', '')
    return final_string
