def encode_string(s):
    vowels = {'a': '1*', 'e': '5*', 'i': '9*', 'o': '15*', 'u': '21*'}
    result = []

    for i in range(len(s)):
        if s[i].lower() in vowels:
            result.append(vowels[s[i].lower()])
        elif s[i].isalpha():
            if (i + 1 < len(s) and s[i + 1].isalpha() and s[i + 1].lower() not in vowels):
                result.append(s[i]*2)
            else:
                result.append(s[i])
        elif s[i] == ' ':
            result.append('(space)')

    encoded_string = ''.join(result)
    return encoded_string.swapcase()

