def invert_case_reverse_vowels(s):
    vowels = "aeiouAEIOU"
    def reverse_vowels(word):
        # extract vowels from the word and reverse them
        rev_vowels = [char for char in word if char in vowels][::-1]
        result_word = []
        vowel_index = 0
        # reconstruct the word with reversed vowels
        for char in word:
            if char in vowels:
                result_word.append(rev_vowels[vowel_index])
                vowel_index += 1
            else:
                result_word.append(char)
        return ''.join(result_word)

    # break the string into words, invert case and reverse vowels for each word
    inverted_case_words = [reverse_vowels(word).swapcase() for word in s.split(' ')]
    # join modified words back into a string
    return ' '.join(inverted_case_words)

