import re
from datetime import datetime

def parse_structured_data(raw_data):
    pattern = r'^ID: (\d+); Data: ([-\w\s]+); Timestamp: (\d{4}-\d{2}-\d{2} \d{2}:\d{2}:\d{2});$'
    match = re.match(pattern, raw_data)
    if not match:
        return None

    id_str, data_str, timestamp_str = match.groups()

    try:
        datetime.strptime(timestamp_str, '%Y-%m-%d %H:%M:%S')
    except ValueError:
        return None

    return {
        'ID': int(id_str),
        'Data': data_str.strip(),
        'Timestamp': timestamp_str
    }
```

