def analyze_url_components(url_string):
    url_components = {
        'scheme': None, 'subdomain': None, 'domain': None,
        'tld': None, 'port': None, 'path': None,
        'query': None, 'fragment': None
    }

    # Define URL pattern for regular expression
    pattern = re.compile(
        r'^(?:(?P<scheme>[a-z][a-z0-9+\-.]*):)?' # Scheme 
        r'(?://(?P<subdomain>[^:/?#]*\.)?)?' # Subdomain
        r'(?P<domain>[^/?#:]*\.)?' # Domain
        r'(?P<tld>[a-z]{2,63})' # TLD
        r'(?::(?P<port>\d*))?' # Port
        r'(?P<path>/[^?#]*)?' # Path
        r'(?:\?(?P<query>[^#]*))?' # Query
        r'(?:#(?P<fragment>.*))?$' # Fragment
    )

    match = pattern.match(url_string)
    if match:
        components_dict = match.groupdict()

        for key in url_components:
            value = components_dict.get(key)
            if value:
                if key == 'scheme' and not re.match(r'^(http|https)$', value, re.IGNORECASE):
                    value = 'Invalid'
                elif key == 'domain' and not re.match(r'^(([a-zA-Z0-9-]){2,}\.)+([a-zA-Z-]){2,}$', value):
