def parse_custom_string(input_str):
    stack = []
    result_dict = {}
    current_key = None
    current_value = None
    in_key = True

    for char in input_str:
        if char == '(':
            if current_key is not None:  # Start of a nested structure
                stack.append((current_key, current_value))
                current_key, current_value = None, None
            in_key = True
        elif char == ')':
            if stack:  # End of a nested structure
                last_key, last_value = stack.pop()
                if current_value is not None:  # Append current value
                    current_value = f"({current_value})"
                result_dict[last_key] = current_value
                current_key, current_value = last_key, result_dict[last_key]
            else:  # End of the current key-value pair
                if current_key is not None:  # Avoid the case of empty value
                    result_dict[current_key] = current_value
                    current_key, current_value = None, None
            in_key = False
        elif not char.isspace():  # Ignore whitespace
            if char == ',':
                if in_key:
                    in_key = False  # Key has ended, start reading the value
                else:  # Comma separating items in the list
                    in_key = True
                    current_key, current_value = None, None
            else:
                if in_key:
                    if current_key is None:
                        current_key = char
                    else:
                        current_key += char
                else:
                    if current_value is None:
                        current_value = char
                    else:
                        current_value += char

    return result_dict

