def parse_hierarchy(string):
    stack = []
    nested_dict = {}
    current_dict = nested_dict

    for char in string:
        if char == '[':
            new_dict = {}
            current_dict[stack[-1]] = new_dict
            stack.append(stack[-1])
            current_dict = new_dict
        elif char == ']':
            stack.pop()
            if not stack:
                break
            current_dict = current_dict[stack[-1]]
        else:
            stack.append(char)
            current_dict[char] = None

