def parse_complex_string(input_str):
    pairs = {'(': ')', '[': ']', '{': '}'}
    stack = []
    result = {}

    def add_to_hierarchy(value):
        if stack:
            current_dict = result
            for key in stack[:-1]:
                current_dict = current_dict.setdefault(key, {})
            current_dict[stack[-1]] = value
        else:
            result[value] = {}

    def parse_recursive(s, start):
        key = ''
        i = start
        while i < len(s):
            if s[i] in pairs:
                if key:
                    add_to_hierarchy(key)
                    key = ''
                stack.append(s[start:i].strip())
                i = parse_recursive(s, i + 1)
            elif s[i] in pairs.values():
                if key:
                    add_to_hierarchy(key)
                    if stack:
                        stack.pop()
                return i
            elif s[i] == ',':
                if key:
                    add_to_hierarchy(key)
                    key = ''
            else:
                key += s[i]
            i += 1
        if key:  # In case the string doesn't end with a delimiter
            add_to_hierarchy(key)
        return i

    parse_recursive(input_str, 0)
    return result
