def dynamic_vigenere_cipher(mode, message, keyword):
    if not keyword.isalpha():
        raise ValueError("Keyword must consist of alphabetic characters only.")
    if not keyword:
        raise ValueError("Keyword cannot be empty.")

    keyword = keyword.lower()  # Ensure keyword is in lowercase for consistency
    result = []
    keyword_indices = [ord(c) - ord('a') for c in keyword]
    keyword_length = len(keyword)

    def get_vigenere_alphabet(key_index):
        return "".join(chr((i + key_index) % 26 + ord('a')) for i in range(26))

    def shift_character(char, key_char, is_encrypting):
        if char.isalpha():
            vigenere_alphabet = get_vigenere_alphabet(ord(key_char) - ord('a'))
            if is_encrypting:
                if char.isupper():
                    return vigenere_alphabet[ord(char) - ord('A')].upper()
                else:
                    return vigenere_alphabet[ord(char) - ord('a')]
            else:
                if char.isupper():
                    original_index = vigenere_alphabet.upper().index(char)
                else:
                    original_index = vigenere_alphabet.index(char)
                return chr(original_index + ord('A') if char.isupper() else ord('a'))
        else:
            return char

    is_encrypting = mode.lower() == 'encrypt'
    for i, char in enumerate(message):
        key_char = keyword[i % keyword_length]
        result_char = shift_character(char, key_char, is_encrypting)
        result.append(result_char)

    return ''.join(result)

