                       'F': '..-.', 'G': '--.', 'H': '....', 'I': '..', 'J': '.---', 
                       'K': '-.-', 'L': '.-..', 'M': '--', 'N': '-.', 'O': '---', 
                       'P': '.--.', 'Q': '--.-', 'R': '.-.', 'S': '...', 'T': '-', 
