                       '0': '-----', ', ': '--..--', '.': '.-.-.-', '?': '..--..', 
                       '/': '-..-.', '-': '-....-', '(': '-.--.', ')': '-.--.-', 
                       ' ': '/', 'ERROR': '........'}

    if mode == 'to_morse':
        # Check for invalid characters
        for char in input_string.upper():
            if char not in MORSE_CODE_DICT:
                raise ValueError(f"Character '{char}' cannot be encoded in Morse code.")
        # Translate to Morse code
        return ' '.join(MORSE_CODE_DICT[char] for char in input_string.upper())

    elif mode == 'from_morse':
        # Validate Morse code
        morse_chars = input_string.split(' ')
        for mchar in morse_chars:
            if mchar and mchar not in MORSE_CODE_DICT.values():
                raise ValueError(f"Sequence '{mchar}' is not valid Morse code.")
        # Translate to readable text
        inverse_morse_dict = {value: key for key, value in MORSE_CODE_DICT.items()}
        return ''.join(inverse_morse_dict.get(char, '') for char in morse_chars).replace('/', ' ')

    else:
        raise ValueError("Invalid mode. Use 'to_morse' or 'from_morse'.")
