def reciprocal_cipher(mode, message, shift):
    if not isinstance(shift, int) or shift < 0:
        return "Shift value must be a non-negative integer"

    if not message or shift == 0:
        return "Message and shift value cannot be empty"

    alphabet = 'abcdefghijklmnopqrstuvwxyz'
    shifted_alphabet = alphabet[shift:] + alphabet[:shift]
    alphabet_upper = alphabet.upper()
    shifted_alphabet_upper = shifted_alphabet.upper()

    if mode == 'encrypt':
        encrypted_message = ''
        for char in message:
            if char.isalpha():
                if char.islower():
                    idx = alphabet.index(char)
                    encrypted_message += shifted_alphabet[idx]
                else:
                    idx = alphabet_upper.index(char)
                    encrypted_message += shifted_alphabet_upper[idx]
            else:
                encrypted_message += char
        return encrypted_message
    elif mode == 'decrypt':
