def digraph_trigraph_similarity(str1, str2):
    # create dictionaries to store frequency of digraphs and trigraphs
    digraph_freq_1 = {}
    digraph_freq_2 = {}
    trigraph_freq_1 = {}
    trigraph_freq_2 = {}

    # calculate frequency of digraphs and trigraphs for str1
    for i in range(len(str1)-1):
        digraph = str1[i:i+2]
        if digraph not in digraph_freq_1:
            digraph_freq_1[digraph] = 1
        else:
            digraph_freq_1[digraph] += 1

        if i < len(str1)-2:
            trigraph = str1[i:i+3]
            if trigraph not in trigraph_freq_1:
                trigraph_freq_1[trigraph] = 1
            else:
                trigraph_freq_1[trigraph] += 1

    # calculate frequency of digraphs and trigraphs for str2
    for i in range(len(str2)-1):
        digraph = str2[i:i+2]
        if digraph not in digraph_freq_2:
            digraph_freq_2[digraph] = 1
        else:
            digraph_freq_2[digraph] += 1

        if i < len(str2)-2:
            trigraph = str2[i:i+3]
            if trigraph not in trigraph_freq_2:
                trigraph_freq_2[trigraph] = 1
            else:
                trigraph_freq_2[trigraph] += 1

    # calculate similarity score
    matching_digraphs = set(digraph_freq_1.keys()).intersection(set(digraph_freq_2.keys()))
    matching_trigraphs = set(trigraph_freq_1.keys()).intersection(set(trigraph_freq_2.keys()))

    numerator = sum(min(digraph_freq_1[d], digraph_freq_2[d]) for d in matching_digraphs) + \
                sum(min(trigraph_freq_1[t], trigraph_freq_2[t]) for t in matching_trigraphs)
    denominator = len(set(list(digraph_freq_1.keys()) + list(digraph_freq_2.keys()))) + \
                  len(set(list(trigraph_freq_1.keys()) + list(trigraph_freq_2.keys())))

    similarity = float(numerator) / float(denominator)
    similarity = round(similarity, 3)

    return similarity
