from difflib import SequenceMatcher

def levenshtein_distance(s1, s2):
    if len(s1) < len(s2):
        return levenshtein_distance(s2, s1)
    if len(s2) == 0:
        return len(s1)
    previous_row = range(len(s2) + 1)
    for i, c1 in enumerate(s1):
        current_row = [i + 1]
        for j, c2 in enumerate(s2):
            insertions = previous_row[j + 1] + 1
            deletions = current_row[j] + 1
            substitutions = previous_row[j] + (c1 != c2)
            current_row.append(min(insertions, deletions, substitutions))
        previous_row = current_row
    return previous_row[-1]

def find_nearest_substring(pattern, text):
    min_distance = float('inf')
    nearest_substring = ""
    nearest_index = -1
    for i in range(len(text) - len(pattern) + 1):
        substring = text[i:i+len(pattern)]
        distance = levenshtein_distance(pattern, substring)
        if distance < min_distance:
            min_distance = distance
            nearest_substring = substring
            nearest_index = i
    return nearest_substring, nearest_index

def compare_substring_patterns(pattern, text):
    alignments = []
    mismatches = []
    matcher = SequenceMatcher(None, pattern, text)
    match_quality = 0

    for match in matcher.get_matching_blocks():
        if match.size > 0:
            start_index = match.b
            aligned_substring = text[start_index:start_index + match.size]
            alignments.append((start_index, aligned_substring))
            match_quality += len(aligned_substring)

    pointer = 0
    for alignment in alignments:
        start = alignment[0]
        end = start + len(alignment[1])
        while pointer < start:
            mismatch = pattern[pointer:start]
            nearest_substring, nearest_index = find_nearest_substring(mismatch, text)
            mismatches.append((mismatch, nearest_substring, pointer, nearest_index))
            pointer = start
        pointer = end
    if pointer < len(pattern):
        mismatch = pattern[pointer:]
        nearest_substring, nearest_index = find_nearest_substring(mismatch, text)
        mismatches.append((mismatch, nearest_substring, pointer, nearest_index))

    if match_quality == len(pattern):
        analysis = 'Perfect'
    elif match_quality > len(pattern) / 2:
        analysis = 'Good'
    elif match_quality > 0:
        analysis = 'Fair'
    else:
        analysis = 'Poor'

    return {'alignments': alignments, 'mismatches': mismatches, 'analysis': analysis}
