def multi_level_substring_prevalence(string_a, string_b, level):
    # Check if level is nonsensical
    if level <= 0 or level > min(len(string_a), len(string_b)):
        raise ValueError("Level value is non-positive or exceeds string lengths")

    # Initialize the result dictionary
    result = {}

