    for i in range(1, level + 1):
        a_substrings = set()
        b_substrings = set()

        # Generate all substrings of length i from string_a
        for j in range(len(string_a) - i + 1):
            a_substrings.add(string_a[j:j+i])

        # Generate all substrings of length i from string_b
        for j in range(len(string_b) - i + 1):
            b_substrings.add(string_b[j:j+i])

        # Calculate common substrings of length i
        common_substrings = a_substrings.intersection(b_substrings)

        # Store the total_occurrences and unique_matches in the result dictionary
        result[i] = {
            'total_occurrences': len(common_substrings),
            'unique_matches': list(common_substrings)
        }

    return result
