    weekend_multiplier = 1.5 if day_of_week.lower() in ['saturday', 'sunday'] else 1
    rates_per_hour = {'car': 2, 'motorcycle': 1, 'bus': 3}
    flat_rates = {'car': 15, 'motorcycle': 10, 'bus': 20}

