def special_day_check(current_date):
    if current_date.month == 2 and current_date.day == 29:
        return 'Leap Day'
    elif current_date.month == 10:
        first_day_of_oct = datetime(current_date.year, 10, 1)
        # Find the first Monday of October
        first_monday_of_oct = first_day_of_oct + timedelta(days=(7-first_day_of_oct.weekday()) % 7)
        # Check if current date is the second Monday of October
        if current_date == first_monday_of_oct + timedelta(days=7):
            return 'Thanksgiving'
    elif current_date.month == 12 and current_date.day == 25:
        previous_year = current_date.year - 1
        # Check if the previous year was a leap year
        if (previous_year % 4 == 0 and previous_year % 100 != 0) or (previous_year % 400 == 0):
            return 'Leap Year Christmas'
        else:
            return 'Regular Christmas'
    else:
        return 'Regular Day'

# Remember this function expects a datetime object passed as the argument for current_date.
