import json

class InvalidJsonError(Exception):
    pass

class EmptyJsonError(Exception):
    pass

class NullJsonError(Exception):
    pass

def parse_json(json_string):
    try:
        if not json_string:
            raise EmptyJsonError("JSON string is empty.")

        obj = json.loads(json_string)
        if obj is None:
            raise NullJsonError("JSON object is null.")

        return obj
    except json.JSONDecodeError as exc:
        raise InvalidJsonError(f"Invalid JSON: {exc}")
