import os

def browse_directory(base_path):
    try:
        while True:
            print(f"Contents of the current directory '{base_path}':")
            try:
                for entry in os.listdir(base_path):
                    print(entry)
            except PermissionError:
                print("Permission denied")
                break
            except FileNotFoundError:
                print("Directory not found")
                break
            except OSError:
                print("Navigation error")
                break

            user_input = input("Enter a directory to navigate to, '..' to go up a level, or 'q' to quit: ").strip()

            if user_input.lower() == 'q':
                break
            elif user_input == '..':
                base_path = os.path.dirname(base_path)
            else:
                new_path = os.path.join(base_path, user_input)
                if not os.path.isdir(new_path):
                    print("Not a directory")
                else:
                    base_path = new_path
    except KeyboardInterrupt:
        print("Operation cancelled")
